import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";

import "./App.css";

import { Grid, Button, Modal, Typography } from "@material-ui/core";
import {
  AddTaskFormContainer,
  SortPopupContainer,
  TaskListContainer,
} from "./components";

import {
  fetchTasks,
  addTask,
  removeTask,
  setTaskStatus,
  setSortType,
} from "./redux/actions/tasks";

const useStyles = makeStyles({
  mainContainer: {
    marginTop: 20,
  },
  addTaskBtn: {
    width: 150,
  },
  modal: {
    alignItems: "center",
    margin: "150px auto",
    width: 400,
    maxHeight: 450,
    paddingBottom: 30,

    backgroundColor: "#f8f8f8",
    border: "2px solid #000",
    boxShadow: "#0f0f0f",
  },
  modalTitle: {
    width: "100%",
    margin: "20px 0",
    fontSize: 32,
    textAlign: "center",
  },
});

function App() {
  const dispatch = useDispatch();
  const classes = useStyles();

  const sortTypes = [
    { id: 1, type: "Due-date" },
    { id: 2, type: "Status" },
  ];

  const { items, sortType } = useSelector(({ tasks }) => tasks);

  const onSelectType = (id) => {
    dispatch(setSortType(id));
  };

  const onAddTask = (obj) => {
    dispatch(addTask(obj));
  };

  useEffect(() => {
    dispatch(fetchTasks(sortType));
  }, [sortType]);

  return (
    <Grid
      className={classes.mainContainer}
      container
      direction="column"
      alignItems="center"
    >
      <Button
        className={classes.addTaskBtn}
        onClick={handleOpenModal}
        size="large"
        variant="contained"
        color="primary"
      >
        Add Task
      </Button>

      <SortPopupContainer
        sortType={sortType}
        items={sortTypes}
        onSelectType={onSelectType}
      />
      {items.length ? (
        <TaskListContainer items={items} />
      ) : (
        <Typography className={classes.modalTitle}>
          You don't have any tasks yet
        </Typography>
      )}

      <Modal open={openModal} onClose={onCloseModal}>
        <Grid container direction="column" className={classes.modal}>
          <Typography className={classes.modalTitle}>New Task</Typography>
          <AddTaskFormContainer />
        </Grid>
      </Modal>
    </Grid>
  );
}

export default App;
