import React, { useState } from "react";

import AddTaskForm from "./AddTaskForm";

function AddTaskFormContainer({ onCloseModal, onAddTask }) {
  const [isFormValid, setIsFormValid] = useState(false);

  const [formInput, setFormInput] = useState({
    status: "pending",
    task: "",
    dueDate: "",
  });

  const isInputValid = (value) => {
    return value.length >= 3 ? true : value === "" ? null : false;
  };

  const checkFormValidation = (obj) => {
    let isValid = Object.values(obj)
      .map((value) => isInputValid(value))
      .every((isValid) => isValid);
    setIsFormValid(isValid);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      handleAddTask(formInput);
      onCloseModal();
    }
  };

  useEffect(() => {
    checkFormValidation(formInput);
  }, [formInput]);

  return <AddTaskForm onCloseModal={onCloseModal} onAddTask={onAddTask} />;
}

export default AddTaskFormContainer;
